mapper.insert:
INSERT MESSAGE ( CHANNEL_ID
               , CONTENT
               , SENDER
               , SEND_DATE
               , MESSAGE_TYPE
      ) VALUES ( #{channel_id}
               , #{content}
               , #{sender}
               , #{send_date}
               , #{message_type}
      )

mapper.sql:
( SELECT SEND_DATE
    FROM MESSAGE
   WHERE ID = #{cursorId}
)

mapper.delete:
UPDATE MESSAGE 
   SET DELETE_YN = 'Y' 
 WHERE ID = #{id}
