mapper.select:

WHERE MEM.EMAIL = ( SELECT MEMBER_EMAIL
                      FROM SNS_INFO
                     WHERE IDENTIFIER = #{id}
      )


WHERE MEM.EMAIL NOT IN ( SELECT MEMBER_EMAIL
                           FROM JOININFO
                          WHERE CHANNEL_ID = #{channel_id}
      )
  AND MEM.EMAIL != 'root'

mapper.insert:
INSERT MEMBER ( NAME
              , PHONE
              , EMAIL
              , PICTURE
              , PASSWORD
     ) VALUES ( #{name}
              , #{phone}
              , #{email}
              , #{picture}
              , #{password}
     )

mapper.update:
UPDATE MEMBER SET NAME = #{name}
                , PHONE = #{phone}
                , PICTURE = #{picture}
            WHERE EMAIL = #{email}
